sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // si on n'a rien modifié on sort
    if (newValue === '') {
        return;
    }

    // on récupère la valeur du champ What needed pour la remettre s'il y en avait déjà une
    var whatneeded = g_form.getValue('u_what_needed');

    // on supprime toutes les options du champs What needed
    g_form.clearOptions('u_what_needed');

    // pour chaque valeur de Request type on ajoute les options de What needed qui correspondent
    if (newValue == 'hr') {
        g_form.addOption('u_what_needed', 'hr1', 'Human Ressources 1');
        g_form.addOption('u_what_needed', 'hr2', 'Human Ressources 2');
        g_form.addOption('u_what_needed', 'other', 'Other');
    }
    if (newValue == 'facilities') {
        g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
        g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
        g_form.addOption('u_what_needed', 'other', 'Other');
    }
    if (newValue == 'legal') {
        g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
        g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
        g_form.addOption('u_what_needed', 'other', 'Other');
    }

	// si le formulaire est en chargement et n'est pas un nouveau document, on met la valeur existante
	if(isLoading && !g_form.isNewRecord()) {
		g_form.setValue('u_what_needed', whatneeded);
	}

}
